import React, { useEffect, useState } from 'react';
import { graphqlFetch } from '../utils/graphql';

const MEDICINE_GQL = 'http://localhost:8004/graphql';

const Medicine = ({ appointmentId, isDoctor, onPrescriptionAdded }) => {
  const [medicines, setMedicines] = useState([]);
  const [prescription, setPrescription] = useState([]); // [{medicine_id, quantity}]
  const [successMsg, setSuccessMsg] = useState('');
  const [errorMsg, setErrorMsg] = useState('');

  useEffect(() => {
    const query = `query { medicines { id name price description } }`;
    graphqlFetch(MEDICINE_GQL, query).then(data => setMedicines(data.medicines));
    if (!isDoctor && appointmentId) {
      // Ambil resep yang sudah ada untuk appointment ini
      const receiptQuery = `query($appointmentId: Int!) { getReceipt(appointmentId: $appointmentId) { medicines { name quantity price_per_unit total } total_amount } }`;
      graphqlFetch(MEDICINE_GQL, receiptQuery, { appointmentId: appointmentId }).then(data => {
        setPrescription((data.getReceipt && data.getReceipt.medicines) || []);
      });
    }
  }, [appointmentId, isDoctor]);

  const handleAddToPrescription = (medicine_id) => {
    setPrescription(prev => {
      if (prev.find(p => p.medicine_id === medicine_id)) return prev;
      return [...prev, { medicine_id, quantity: 1 }];
    });
  };

  const handleQuantityChange = (medicine_id, qty) => {
    setPrescription(prev => prev.map(p => p.medicine_id === medicine_id ? { ...p, quantity: qty } : p));
  };

  const handleRemove = (medicine_id) => {
    setPrescription(prev => prev.filter(p => p.medicine_id !== medicine_id));
  };

  const handleSubmitPrescription = async () => {
    setSuccessMsg('');
    setErrorMsg('');
    try {
      const mutation = `mutation($appointmentId: Int!, $medicines: [Int!]!, $quantities: [Int!]!) { addMedicinesToAppointment(appointmentId: $appointmentId, medicines: $medicines, quantities: $quantities) }`;
      await graphqlFetch(MEDICINE_GQL, mutation, {
        appointmentId: appointmentId,
        medicines: prescription.map(p => p.medicine_id),
        quantities: prescription.map(p => parseInt(p.quantity))
      });
      setSuccessMsg('Resep berhasil ditambahkan ke appointment.');
      setPrescription([]);
      if (onPrescriptionAdded) onPrescriptionAdded();
    } catch (err) {
      setErrorMsg('Gagal menambahkan resep.');
    }
  };

  return (
    <div style={{ margin: '24px 0' }}>
      <h3>Daftar Obat</h3>
      <ul>
        {medicines.map(m => (
          <li key={m.id} style={{ marginBottom: 8 }}>
            <b>{m.name}</b> (Rp{m.price}) - {m.description}
            {isDoctor && (
              <button style={{ marginLeft: 8 }} onClick={() => handleAddToPrescription(m.id)} disabled={!!prescription.find(p => p.medicine_id === m.id)}>
                Tambah ke Resep
              </button>
            )}
          </li>
        ))}
      </ul>
      {isDoctor && prescription.length > 0 && (
        <div style={{ marginTop: 16 }}>
          <h4>Resep yang akan diberikan:</h4>
          {prescription.map(p => {
            const med = medicines.find(m => m.id === p.medicine_id);
            return (
              <div key={p.medicine_id}>
                {med && med.name} -
                <input type="number" min={1} value={p.quantity} onChange={e => handleQuantityChange(p.medicine_id, e.target.value)} style={{ width: 50, marginLeft: 8 }} />
                <button style={{ marginLeft: 8 }} onClick={() => handleRemove(p.medicine_id)}>Hapus</button>
              </div>
            );
          })}
          <button style={{ marginTop: 12 }} onClick={handleSubmitPrescription}>Simpan Resep ke Appointment</button>
          {successMsg && <div style={{ color: 'green', marginTop: 8 }}>{successMsg}</div>}
          {errorMsg && <div style={{ color: 'red', marginTop: 8 }}>{errorMsg}</div>}
        </div>
      )}
    </div>
  );
};

export default Medicine;
